const $actionContainer = document.querySelector('#action');
const $dramaContainer = document.getElementById('drama');
const $animationContainer = document.getElementById('animation');

const $home = document.getElementById('home');
const $featuringContainer = document.getElementById('featuring');
const $form = document.getElementById('form');

const $modal = document.getElementById('modal');
const $overlay = document.getElementById('overlay');
const $hideModal = document.getElementById('hide-modal');

const $modalTitle = $modal.querySelector('h1');
const $modalImg = $modal.querySelector('img');
const $modalDescription = $modal.querySelector('p');

const $playlistFriends = document.querySelector('.playlistFriends');
const $myPlaylist = document.querySelector('.myPlaylist');

(async function load() {
  const BASE_API_USER = 'https://randomuser.me/api/?results=7';
  const BASE_API_MOVIE = 'https://yts.am/api/v2/list_movies.json';

  async function getDataMovie(url) {
    const response = await fetch(url);
    const data = await response.json();
    if (data.data.movie_count > 0) {
      return data;
    }
    // si no hay pelis aqui continua
    throw new Error('No se econtro ningun resultado');
  }

  async function getDataUser(url) {
    const response = await fetch(url);
    const data = await response.json();
    return data;
  }

  async function getBestMovies(BASE_API_MOVIE) {
    const response = await fetch(`${BASE_API_MOVIE}?minimum_rating=9&limit=8`);
    const {
      data: { movies },
    } = await response.json();
    return movies;
  }

  function setAttributes($element, attributes) {
    for (const attribute in attributes) {
      $element.setAttribute(attribute, attributes[attribute]);
    }
  }

  function featureVideoTemplate(movie) {
    return ` 
      <div class="featuring">
        <div class="featuring-image">
          <img src="${movie.medium_cover_image}" alt=""/>
        </div>
        <div class="featuring-content">
          <p class="featuring-title">Pelicula Encontrada</p>
          <p class="featuring-album">${movie.title_english}</p>
        </div>
      </div> `;
  }

  function videoItemTemplate(movie, category) {
    return `
      <div class="primaryPlaylistItem" data-id="${
        movie.id
      }" data-category="${category}" >
        <div class="primaryPlaylistItem-image">
          <img src="${movie.medium_cover_image}" />
        </div>
        <h4 class="primaryPlaylistItem-title">
          ${movie.title_english}
        </h4>
      </div>`;
  }

  const firstLetterUpper = (word) =>
    `${word.charAt(0).toUpperCase()}${word.slice(1)}`;

  function userItemTemplate(user) {
    const name = firstLetterUpper(user.name.first);
    const lastName = firstLetterUpper(user.name.last);
    return `<li class="playlistFriends-item">
    <a href="#">
    <img src="${user.picture.thumbnail}" alt="user photo" />
    <span>
    ${name} ${lastName}
    </span>
    </a>
    </li>`;
  }

  function bestMoviesTemplate(movie) {
    return `<li class="myPlaylist-item">
              <a href="#">
                <span>
                   ${movie.title_english}
                </span>
              </a>
            </li>`;
  }

  function createTemplate(HTMLString) {
    const $html = document.implementation.createHTMLDocument();
    $html.body.innerHTML = HTMLString;
    return $html.body.children[0];
  }

  function renderMovieList(list, $container, category) {
    $container.children[0].remove();
    list.forEach((movie) => {
      const HTMLString = videoItemTemplate(movie, category);
      const movieElement = createTemplate(HTMLString);
      $container.append(movieElement);
      const imageMovie = movieElement.querySelector('img');
      imageMovie.addEventListener('load', (event) => {
        event.srcElement.classList.add('fadeIn');
      });
      addEventClick(movieElement);
    });
  }

  function renderUser(userList, $playlistFriends) {
    $playlistFriends.innerHTML = '';
    userList.forEach((user) => {
      const HTMLString = userItemTemplate(user);
      const userElement = createTemplate(HTMLString);
      $playlistFriends.append(userElement);
    });
  }

  function renderBestMoviesList(bestMoviesList, $myPlaylist) {
    bestMoviesList.forEach((movie) => {
      const HTMLString = bestMoviesTemplate(movie);
      const movieElement = createTemplate(HTMLString);
      $myPlaylist.append(movieElement);
    });
  }

  /**
   * Obteniendo datos del API
   * Peticion -> render
   */

  async function cacheExistMovie(category) {
    const listName = `${category}List`;
    const cacheList = localStorage.getItem(listName);
    if (cacheList) {
      return JSON.parse(cacheList);
    }
    const {
      data: { movies },
    } = await getDataMovie(`${BASE_API_MOVIE}?genre=${category}`);
    localStorage.setItem(listName, JSON.stringify(movies));
    return movies;
  }

  const bestMoviesList = await getBestMovies(BASE_API_MOVIE);
  renderBestMoviesList(bestMoviesList, $myPlaylist);

  const { results: userList } = await getDataUser(BASE_API_USER);
  renderUser(userList, $playlistFriends);

  const actionList = await cacheExistMovie('action');
  renderMovieList(actionList, $actionContainer, 'action');

  const dramaList = await cacheExistMovie('drama');
  renderMovieList(dramaList, $dramaContainer, 'drama');

  const animationList = await cacheExistMovie('animation');
  renderMovieList(animationList, $animationContainer, 'animation');

  /**
   * Modal Handler
   */
  const findById = (list, id) =>
    list.find((movie) => movie.id === parseInt(id, 10));

  function findMovie(id, category) {
    switch (category) {
      case 'action':
        return findById(actionList, id);
      case 'drama':
        return findById(dramaList, id);
      case 'animation':
        return findById(animationList, id);
    }
  }

  function showModal($element) {
    $overlay.classList.add('active');
    $modal.style.animation = 'modalIn .8s forwards';
    const id = $element.dataset.id;
    const category = $element.dataset.category;
    const dataMovie = findMovie(id, category);
    $modalTitle.textContent = dataMovie.title_long;
    $modalDescription.textContent = dataMovie.summary;
    $modalImg.setAttribute('src', dataMovie.medium_cover_image);
  }

  function hideModal() {
    $overlay.classList.remove('active');
    $modal.style.animation = 'modalOut .8s forwards';
  }

  $hideModal.addEventListener('click', hideModal);

  function addEventClick($element) {
    $element.addEventListener('click', function() {
      showModal($element);
    });
  }

  /**
   * Form Handler
   */

  $form.addEventListener('submit', async (event) => {
    event.preventDefault();
    $home.classList.add('search-active');
    const $loader = document.createElement('img');
    setAttributes($loader, {
      src: 'src/images/loader.gif',
      height: 50,
      width: 50,
    });
    $featuringContainer.append($loader);
    const dataForm = new FormData($form);
    try {
      const {
        data: { movies },
      } = await getDataMovie(
        `${BASE_API_MOVIE}?limit=1&query_term=${dataForm.get('name')}`
      );
      const movieHTMLString = featureVideoTemplate(movies[0]);
      $featuringContainer.innerHTML = movieHTMLString;
    } catch (error) {
      alert(error.message);
      $loader.remove;
      $home.classList.remove('search-active');
    }
  });
})();
